const Ahorro = require('../models/ahorro');// Suponiendo que tengas un modelo llamado 'ahorro' para representar los datos en la base de datos

function agregarAhorro(req, res) {
  const { txtCedula, txtNombre, txtApellido1, txtApellido2, txtDeposito } = req.body;

  // Crear un nuevo objeto Ahorro con los datos recibidos
  const nuevoAhorro = new Ahorro({
    cedula: txtCedula,
    nombre: txtNombre,
    apellido1: txtApellido1,
    apellido2: txtApellido2,
    deposito: txtDeposito,
  });

  // Guardar el nuevo ahorro en la base de datos
  nuevoAhorro.save()
    .then(() => {
      res.redirect('/index'); // Redirigir al usuario de vuelta a la página de inicio después de la inserción
    })
    .catch((error) => {
      // Manejar el error, por ejemplo, renderizar un mensaje de error en la página
      res.render('error', { error });
    });
}

